import React, { Component } from 'react'
import { Form, Button, Container } from 'react-bootstrap'
import API from '../../utils/API'
//import { Redirect } from 'react-router-dom'


class ProjectForm extends React.Component {

    // STATE
    constructor(props) {
        super(props)
        this.state = {

            projectTitle: '',
            creator: '',
            startDate: '',
            deadLine: '',
            todos: '',
            assignee: '',
            isActive: false

        }
    }


handleShow = () => {
    this.setState({
        isActive: true
    })
}

handleHide = () => {
    this.setState({
        isActive: false
    })
}


    // handleInputChange
    handleInputChange = (e) => {
        const { name, value } = e.target;
        this.setState({
            [name]: value
        });
    };

    // handleFormSubmit
    handleFormSubmit = (e) => {
        e.preventDefault();
        this.setState({ projects: [] });
        console.log(this.state);

        API.saveProject({
            projectTitle: this.state.projectTitle,
            creator: this.state.creator,
            startDate: this.state.startDate,
            deadLine: this.state.deadLine,
            todos: this.state.todos,
            assignee: this.state.assignee
        })

            .then(obj => console.log(obj))
            .catch(err => console.log(err));

            window.location.reload();
    };



    render() {
        return (
            <React.Fragment>
                <Container>

                    <Form>
                        <Form.Group controlId="formUserName">
                            <Form.Label>Project Administrator</Form.Label>
                            <Form.Control
                                name="creator"
                                onChange={this.handleInputChange}
                                type="text"
                                placeholder="Enter username"
                            />
                        </Form.Group>

                        <Form.Group controlId="formProjectTitle">
                            <Form.Label>Project Name</Form.Label>
                            <Form.Control
                                name="projectTitle"
                                onChange={this.handleInputChange}
                                type="text"
                                placeholder="What are you working on?"
                            />
                        </Form.Group>

                        <Form.Group controlId="formStartDate">
                            <Form.Label>Start Date</Form.Label>
                            <Form.Control
                                name="startDate"
                                onChange={this.handleInputChange}
                                type="text"
                                placeholder="When was this assigned?"
                            />
                        </Form.Group>

                        <Form.Group controlId="formDeadLine">
                            <Form.Label>Deadline</Form.Label>
                            <Form.Control
                                name="deadLine"
                                onChange={this.handleInputChange}
                                type="text"
                                placeholder="When is this to be completed by?"
                            />
                        </Form.Group>

                        <Form.Group controlId="formObjectives">
                            <Form.Label>Task at Hand</Form.Label>
                            <Form.Control 
                                name="todos"
                                onChange={this.handleInputChange}
                                type="text"
                                placeholder="TODO"
                            />

                        </Form.Group>

                        <Form.Group controlId="formObjectives">
                            <Form.Label>Assignee</Form.Label>
                            <Form.Control 
                                name="assignee"
                                onChange={this.handleInputChange}
                                type="text"
                                placeholder="Who's doing this task?"
                            />

                        </Form.Group>

                        <Button variant="primary" type="submit" onClick={this.handleFormSubmit} >
                            Submit
                </Button>
                    </Form>
                </Container>

            </React.Fragment>
        );
    };


}

export default ProjectForm



