import React, { Component } from 'react'
import { Container } from 'react-bootstrap'
import './style.css'
import API from '../../utils/API';
//import UserProjectScope from '../UserProjectScope'
//import ProjectForm from '../ProjectForm'
import { Form, Button } from 'react-bootstrap'
import ProjectDetails from '../ProjectDetails'


class ProfileGrid extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            userProjects: [],
            clickedPDBtn: false,

            projectTitle: '',
            creator: '',
            startDate: '',
            deadLine: '',
            todos: '',
            assignee: '',
            clickedFBtn: false
        }
        console.log(props)

    }

    handleInputChange = (e) => {
        const { name, value } = e.target;
        this.setState({
            [name]: value
        });
    };

     // handleFormSubmit
     handleFormSubmit = (e) => {
        e.preventDefault();
        this.setState({ projects: [] });
        console.log(this.state);

        API.saveProject({
            projectTitle: this.state.projectTitle,
            creator: this.state.creator,
            startDate: this.state.startDate,
            deadLine: this.state.deadLine,
            todos: this.state.todos,
            assignee: this.state.assignee
        })

            .then(obj => console.log(obj))
            .catch(err => console.log(err));

            window.location.reload();
    };



    componentDidMount() {

       this.getProjects();
        console.log(this.state.userProjects)
    }

    getProjects = () => {

        API.getProjects()
            .then(e => 
                this.setState({ 
                    userProjects: e.data
                 })
            )
            .catch(err => console.log(err));

    }



    // method for hide form, show data
    toggleData = () => {
        this.setState({
            clickedPDBtn: !this.state.clickedPDBtn
        })
    }
    // method for hide data, show form
    toggleForm = () => {
        this.setState({
            clickedFBtn: !this.state.clickedFBtn
        })
    }



    render() {
        const projects = this.state.userProjects;
        console.log(projects);
        const Details = <ProjectDetails />
        let display;



        return (
            <React.Fragment>
                <Container>
                    <div className="leftpane">
                    <h1>Project List</h1> 
                        <br />
                        <button type="button" >Create New Project</button>
                        <br />
                        <br/>
                        {projects.map((project) => {
                            return (
                                <button 
                                type='button' 
                                key={project.id}
                                onClick={this.toggleData}>{project.projectTitle}</button>
                            )
                            })}
                    </div>

                    <div className="middlepane">
                    <h1>Project Form</h1> <br />
                    {/* \/ this form just needs a 'hidden' property to then be changed
                            to be able to change the rendered component onClick!!!! 
                        */}
                    <Form>
                        <Form.Group controlId="formUserName">
                            <Form.Label>Project Administrator</Form.Label>
                            <Form.Control
                                name="creator"
                                onChange={this.handleInputChange}
                                type="text"
                                placeholder="Enter username"
                            />
                        </Form.Group>

                        <Form.Group controlId="formProjectTitle">
                            <Form.Label>Project Name</Form.Label>
                            <Form.Control
                                name="projectTitle"
                                onChange={this.handleInputChange}
                                type="text"
                                placeholder="What are you working on?"
                            />
                        </Form.Group>

                        <Form.Group controlId="formStartDate">
                            <Form.Label>Start Date</Form.Label>
                            <Form.Control
                                name="startDate"
                                onChange={this.handleInputChange}
                                type="text"
                                placeholder="When was this assigned?"
                            />
                        </Form.Group>

                        <Form.Group controlId="formDeadLine">
                            <Form.Label>Deadline</Form.Label>
                            <Form.Control
                                name="deadLine"
                                onChange={this.handleInputChange}
                                type="text"
                                placeholder="When is this to be completed by?"
                            />
                        </Form.Group>

                        <Form.Group controlId="formObjectives">
                            <Form.Label>Task at Hand</Form.Label>
                            <Form.Control 
                                name="todos"
                                onChange={this.handleInputChange}
                                type="text"
                                placeholder="TODO"
                            />

                        </Form.Group>

                        <Form.Group controlId="formObjectives">
                            <Form.Label>Assignee</Form.Label>
                            <Form.Control 
                                name="assignee"
                                onChange={this.handleInputChange}
                                type="text"
                                placeholder="Who's doing this task?"
                            />

                        </Form.Group>

                        <Button variant="primary" type="submit" onClick={this.handleFormSubmit} >
                            Submit
                </Button>
                    </Form>

                    </div>


                </Container>
            </React.Fragment>
        )
    }
}


export default ProfileGrid
