import React, { Component } from 'react'
import { Container } from 'react-bootstrap'
import './style.css'
import API from '../../utils/API';
//import UserProjectScope from '../UserProjectScope'
import ProjectForm from '../ProjectForm'
import ProjectDetails from '../ProjectDetails'


class ProfileGrid extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            userProjects: [],
            detailShow: false
        }
        console.log(props)
        this.showDetail = this.showDetail.bind(this)
    }



    componentDidMount() {

       this.getProjects();
        console.log(this.state.userProjects)
    }

    getProjects = () => {

        API.getProjects()
            .then(e => 
                this.setState({ 
                    userProjects: e.data
                 })
            )
            .catch(err => console.log(err));

    }



    // method for hide form, show data
    // method for hide data, show form

    showDetail = () => {
        return <ProjectDetails />        
    }

    render() {
        const projects = this.state.userProjects;
        console.log(projects)
        const Details = <ProjectDetails />

        return (
            <React.Fragment>

                <Container>
                    <div className="leftpane"><h1>Project List</h1> <br />
                        <button type="button" >Create New Project</button>
                        <br />
                        <br/>
                        {projects.map((project) => {
                            return (
                                <button onClick={this.showDetail} type='button' key={project.id}>{project.projectTitle}</button>
                            )
                            })}
                    </div>

                    <div className="middlepane"><h1>Project Form</h1> <br />
                        <ProjectForm />

                    </div>


                </Container>


            </React.Fragment>
        )
    }
}


export default ProfileGrid
