import React, { Component } from 'react'
import { Container } from 'react-bootstrap'
import './style.css'
import API from '../../utils/API';

// import UserProjectScope from '../UserProjectScope'


class ProfileGrid extends React.Component {
    constructor(props) {
        // hold props for displaying the created profiles
        super(props);
        /*
        */
        this.state = {
            userProjects: []
        }

    }
    componentDidMount() {
        var onMo = this.state;
        API.getProjects((obj)=>console.log(obj));
        this.setState({ userProjects: obj });
        console.log(onMo)
    }

    //const [userProjects, extendProject] = useState(props);
    // /.\ -possibly change 'props to a method that takes intialState as the const *?|?*



    render() {

        return (
            <React.Fragment>

                <Container>
                    <div className="leftpane"><h1>Project List</h1> <br />
                        <p>{this.props.projectTitle}</p> <br />

                    </div>

                    <div className="middlepane"><h1>Project Scope</h1> <br />
                        <h4>PROPS.PROJECT.TITLE</h4> <br />
                        <p>PROPS.PROJECT.CREATOR</p>
                        <h5>BUTTON :: add objective</h5>
                        <h2>OBJECTIVE(s)</h2>
                        <li>in each OBJ-BOX (TASKS, NEW-TASK_BTN, OBJ_TARGET-DATE, ASSIGNEE(s))</li>
                    </div>

                    <div className="rightpane"><h1>Chat Box</h1>
                    </div>
                </Container>


            </React.Fragment>
        )
    }
}


export default ProfileGrid
