import React, { Component }  from 'react'
import { Form, Button, Container } from 'react-bootstrap'

class UserProjectScope extends Component {
    constructor(props) {
        super(props);
        this.state = {
            formShow: false
        }

    }

    // componentDidMount

    // handleCollabsSubmit
    // handleObjectiveAddition
    // handleAssignee
    // handleObjectivePOST
    // handleObjectiveFinalize



    //===========
    // TODOS::
    // createCard to Display intial data
    // * form !!!!
    // >>> + New Objective/Task
            // taskName, taskDescription, assigned:who?@all?
            // >>> + addNewTask
    // >>> + Assignees(GET OTHER PROFILES)
    // >>> + FINALIZE-SUBMIT PROJECT!



    render() {

        // ARGGGG




        return(
            <React.Fragment>
                <h1>SCOPE</h1>

                <Form>
                        <Form.Group controlId="formUserName">
                            <Form.Label style={{ fontWeight: 'bolder' }}>Project Administrator</Form.Label>
                            <Form.Control
                                name="creator"
                                onChange={this.handleInputChange}
                                type="text"
                                placeholder="Enter username"
                            />
                        </Form.Group>

                        <Form.Group controlId="formProjectTitle">
                            <Form.Label style={{ fontWeight: 'bolder' }}>Project Name</Form.Label>
                            <Form.Control
                                name="projectTitle"
                                onChange={this.handleInputChange}
                                type="text"
                                placeholder="What are you working on?"
                            />
                        </Form.Group>

                        <Form.Group controlId="formStartDate">
                            <Form.Label style={{ fontWeight: 'bolder' }}>Start Date</Form.Label>
                            <Form.Control
                                name="startDate"
                                onChange={this.handleInputChange}
                                type="text"
                                placeholder="When was this assigned?"
                            />
                        </Form.Group>

                        <Form.Group controlId="formDeadLine">
                            <Form.Label style={{ fontWeight: 'bolder' }}>Deadline</Form.Label>
                            <Form.Control
                                name="deadLine"
                                onChange={this.handleInputChange}
                                type="text"
                                placeholder="When is this to be completed by?"
                            />
                        </Form.Group>
                        <Button variant="danger" type="submit" onClick={this.handleFormSubmit} >
                            Submit
                </Button>
                    </Form>

            </React.Fragment>
        )
    }

}
export default UserProjectScope
