import React from 'react'
import { Form, Button, Container } from 'react-bootstrap'
import API from '../../utils/API'

class ProjectForm extends React.Component {

    // STATE
        state = {
            projectTitle:'',
            creator: '',
            startDate: '',
            deadLine: '',
            workTime: '',
            commitment: ''
        }
    // handleInputChange
    /*
    handleInputChange = (e) => {
        const { name, value } = e.target;
        this.setState({
            [name]: value
        });
    };

    // handleFormSubmit
  */
    handleFormSubmit = (e) => {
        e.preventDefault();
        if (this.state.creator && this.state.title && this.state.startDate && this.state.deadLine) {
            API.saveProject({
                title: this.state.projectTitle,
                creator: this.state.creator,
                startDate: this.state.startDate,
                deadLine: this.state.deadLine
            })
            .then(res => console.log(res))
            .catch(err => console.log(err));
        };
    };



    render() {
        return (
            <React.Fragment>
                <Container>

        <Form>
        <Form.Group controlId="formUserName">
            <Form.Label>Project Administrator</Form.Label>
                <Form.Control 
                type="text" 
                placeholder="Enter username" 
                />
        </Form.Group>

        <Form.Group controlId="formProjectTitle">
            <Form.Label>Project Name</Form.Label>
                <Form.Control
                type="text" 
                placeholder="What are you working on?" 
                />
        </Form.Group>

       <Form.Group controlId="formStartDate">
                <Form.Label>Start Date</Form.Label>
                <Form.Control 
                type="text" 
                placeholder="When was this assigned?"
                />
       </Form.Group>

       <Form.Group controlId="formDeadLine">
                <Form.Label>Deadline</Form.Label>
                <Form.Control 
                type="text" 
                placeholder="When is this to be completed by?"
                />
       </Form.Group>
       <Form.Group controlId="formWorkData">
            <Form.Label>Hours worked per week?</Form.Label>
            <Form.Control 
            type="text" 
            placeholder="work hours" 
            />
            <Form.Label>User Commitment</Form.Label>
            <Form.Control 
            type="text" 
            placeholder="How many hours can you devote per day to this project?" 
            />
       </Form.Group>


        <Form.Group controlId="formAddCollabz">
                <Form.Label>Collaborators</Form.Label>
                <Form.Control as="select">
                    <option>select...</option>
                    <option>...</option>
                </Form.Control>
        </Form.Group>

        <Button variant="primary" type="submit" onSubmit={this.handleFormSubmit}>
            Submit
        </Button>
        </Form>
        </Container>

            </React.Fragment>
        );
    };


}

export default ProjectForm
