import React, { Component } from 'react'

import FormPersonalDetails from '../FormPersonalDetails'
import FormProjectDetails from '../FormProjectDetails'
import Confirm from '../Confirm'


class Form extends Component {

    state = {
        step: 1,
        userName: '',
        hrsWrkdPWk: '',
        email: '',
        occupation: '',
        city: '',
        ProjectTitle: '',
        Project: [{
            id: 1,
            title: '',
            dateStart: '',
            deadLine: '',
            Collabz: ''
        }]
    }

    // method to proceeed to next step
    nextStep = () => {
        const { step } = this.state;
        this.setState({
            step: step + 1
        })
    }

    // method to go back to previos step
    previousStep = () => {
        const { step } = this.state;
        this.setState({
            step: step - 1
        })
    }

    // method to handle the fields' change
    handleFieldChange = (input) => e => {
        this.setState({ [input]: e.target.value });
    }

    render() {
        const { step } = this.state;
        const { firstName, hrsWrkdPWk, email, occupation, city, ProjectTitle } = this.state;
        const values = { firstName, hrsWrkdPWk, email, occupation, city, ProjectTitle };
        switch(step) {
            case 1:
                return (
                    <FormPersonalDetails 
                        nextStep={this.nextStep}
                        handleFieldChange={this.handleFieldChange}
                        values={values}
                    />
                );
            case 2:
                 return ( <FormProjectDetails 
                    nextStep={this.nextStep}
                    handleFieldChange={this.handleFieldChange}
                    values={values}
                 /> );

            case 3:            
                    return( <h1>Confirm</h1> );
            case 4:
                return( <h1>Success</h1> )        
        }


    }


}

export default Form
